def ner(model, tokenizer, text="海南省长春市朝阳区西中华15号宜家公寓14楼1405室"):
    '''
    以开始的实体类型B-、S-为依据进行切分，即若当前是开始，则为新的单词
    :param model:
    :param tokenizer:
    :param text:
    :return:
    '''
    classifier = pipeline('token-classification', model=model, tokenizer=tokenizer)
    output = classifier(text)
    print(output)
    result_list = []
    last_end = None
    for ret in output:
        entity = ret['entity']
        start = ret['start']
        end = ret['end']
        word = text[start:end]
        if last_end is None and start != 0:
            result_list.append([text[: start], 'O'])
        elif last_end is not None and last_end != start:
            result_list.append([text[last_end: start], 'O'])

        if entity.startswith('B-') or entity.startswith('S-'):
            result_list.append([word, entity[2:]])
        else:
            result_list[-1][0] += word
        last_end = end

    if last_end is not None and text[last_end: ] !='':
        result_list.append([text[last_end: ], 'O'])

    return result_list


